import React, { useState } from "react";
import axios from "axios";

import "./WeatherForecast.css";
import ForecastDay from "./ForecastDay";

export default function WeatherForecast(props) {
  const [loaded, setLoaded] = useState(false);
  const [forecast, setForecast] = useState(null);

  function handleForecastResponse(response) {
    setLoaded(true);
    setForecast(response.data.daily);
  }

  if (loaded) {
    return (
      <div className="oneDay">
        <div className="row ">
          <div className="col">
            <div>{forecast.time}</div>
            <div>
              <WeatherIcon code={forecast.condition.icon} size={40} />
            </div>
            <div className="temp">
              <span>{forecast.temperature.maximum}</span>°{" "}
              <span>{forecast.temperature.minimum}</span>°
            </div>
          </div>
        </div>
      </div>
    );
  } else {
    let apiKey = "36bb04605e1o2a2f7ce6c8t6fa5fe0ba";
    let apiUrl = `https://api.shecodes.io/weather/v1/forecast?query=${props.data.city}&key=${apiKey} `;
    axios.get(apiUrl).then(handleForecastResponse);
  }
}
